import React, { Component } from 'react';
import styles from './App.module.scss';
import Header from './components/Header';
import Selection from './components/Selection';
import ProductList from './components/ProductList';

export default class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      allProducts: [],
      products: []
    };

    this.handleSort = this.handleSort.bind( this );
  }

  componentDidMount() {
    fetch("https://demo3211013.mockable.io/ajmad")
      .then(res => res.json())
      .then(res => this.setState({ allProducts: res, products: res }))
  }

  handleSort( e ) {
    const type = e.target.value;
    const products = this.state.allProducts;
    let result;

    if ( type === 'highest' ) {
      result = products.sort((a, b) => ( a.prices.final_price > b.prices.final_price ) ? 1 : -1)
    }

    if ( type === 'lowest' ) {
      result = products.sort((a, b) => ( a.prices.final_price > b.prices.final_price ) ? 1 : -1)
    }

    this.setState({ products: result });
  }


  render() {

    {
      this.state.products.length && (
        <div className={styles.app}>
          <Header 
            title="Appliances Packages" 
            subcopy="Looking for a great deal on home appliances? Packages are the best bet. Most appliance packages consist of a range, a refrigerator, over-the-range microwave, and dishwasher. However some upscale brands have packages that incorporate wall ovens, cooktops, and integrated refrigerators."
          />
          <Selection handleSort={this.handleSort} />
          <ProductList products={this.state.products} />
        </div>
      )
    }
  }
}
