import React from 'react';
import styles from './Product.module.scss';

const Product = ({
    brand,
    description,
    discount,
    finalPrice,
    listPrice,
    quickShip,
    series,
    src
}) => (
    <div className={styles.container}>
        <p className={styles.description}>{brand} {series} {description}</p>
        <img className={styles.image} src={src} alt={brand} />
        {!quickShip && <img alt="quick shipping available" src="/public/quickship-pdp.png" />}
        <p className={styles.finalPrice}>${finalPrice}</p>
        <p className={styles.listPrice}><span>{discount}</span>${listPrice}</p>
        <button>View Package</button>
    </div>
);

export default Product;
